var prevServings=0,prevFactor=1;getDefault=function(){prevServings=Number(document.getElementById("servings").innerText),prevFactor=1},scaleRecipe=function(e){newServings=e*prevServings/prevFactor,document.getElementById("servings").innerText=newServings,scalefactor=newServings/prevServings,scalable_quantities=document.getElementsByClassName("scalable");for(var s=0;s<scalable_quantities.length;s++)strValue=scalable_quantities[s].innerHTML,strValue.includes("/")?(split=strValue.split("/"),value=parseInt(split[0],10)/parseInt(split[1],10),console.log(value)):value=Number(strValue),scaled=Math.round(value*scalefactor*24)/24,scaled=Number(scaled.toFixed(2)),scalable_quantities[s].innerHTML=scaled;prevServings=newServings,prevFactor=e};