var PS=0,PF=1;getDefault=function(){PS=Number(document.getElementById("servings").innerText),PF=1},scaleRecipe=function(e){newServings=e*PS/PF,document.getElementById("servings").innerText=newServings,SF=newServings/PS,SQ=document.getElementsByClassName("scalable");for(var n=0;n<SQ.length;n++)strValue=SQ[n].innerHTML,scaled=strValue.includes("/")?(split=strValue.split("/"),value=parseInt(split[0],10)/parseInt(split[1],10),scale(value,SF)):(strValue.includes("-")?scaleRange:scale)(strValue,SF),SQ[n].innerHTML=scaled;PS=newServings,PF=e},scaleRange=function(e,n){return split=e.split("-"),lower=scale(split[0],n),upper=scale(split[1],n),lower+"-"+upper},scale=function(e,n){return Number((Math.round(e*n*24)/24).toFixed(2))};