var prevServings=0,prevFactor=1;getDefault=function(){prevServings=Number(document.getElementById("servings").innerText),prevFactor=1},scaleRecipe=function(e){newServings=e*prevServings/prevFactor,document.getElementById("servings").innerText=newServings,scalefactor=newServings/prevServings,scalable_quantities=document.getElementsByClassName("scalable");for(var n=0;n<scalable_quantities.length;n++)strValue=scalable_quantities[n].innerHTML,strValue.includes("/")?(split=strValue.split("/"),value=parseInt(split[0],10)/parseInt(split[1],10),console.log(value)):value=Number(strValue),scaled=Math.round(value*scalefactor*8)/8,scalable_quantities[n].innerHTML=scaled;prevServings=newServings,prevFactor=e};