var PS=0,PF=1;getDefault=function(){PS=Number(document.getElementById("servings").innerText),PF=1},scaleRecipe=function(e){newServings=e*PS/PF,document.getElementById("servings").innerText=newServings,SF=newServings/PS,SQ=document.getElementsByClassName("scalable");for(var s=0;s<SQ.length;s++)strValue=SQ[s].innerHTML,strValue.includes("/")?(split=strValue.split("/"),value=parseInt(split[0],10)/parseInt(split[1],10),sscaled=scale(value,SF)):strValue.includes("-")?scaled=scaleRange(strValue,SF):scaled=scale(strValue,SF),SQ[s].innerHTML=scaled;PS=newServings,PF=e},scaleRange=function(e,s){return split=e.split("-"),lower=scale(split[0],s),upper=scale(split[1],s),lower+"-"+upper},scale=function(e,s){return Number((Math.round(e*s*24)/24).toFixed(2))};