var prevServings=0,prevFactor=1;getDefault=function(){prevServings=Number(document.getElementById("servings").innerText),prevFactor=1},scaleRecipe=function(e){newServings=e*prevServings/prevFactor,document.getElementById("servings").innerText=newServings,scalefactor=newServings/prevServings,scalable_quantities=document.getElementsByClassName("scalable");for(var r=0;r<scalable_quantities.length;r++)strValue=scalable_quantities[r].innerHTML,strValue.includes("/")?(split=strValue.split("/"),value=parseInt(split[0],10)/parseInt(split[1],10)):value=Number(strValue),scaled=Math.round(value*scalefactor*12)/12,scaled=Number(scaled.toFixed(2)),scalable_quantities[r].innerHTML=scaled;prevServings=newServings,prevFactor=e};